import React from 'react';

const Portfolio = () => {
  // Array de objetos que representa los proyectos
  const projects = [
    {
      title: "Proyecto 1",
      description: "Descripción del Proyecto 1.",
      imageUrl: "/ruta/a/imagen1.jpg",
      demoUrl: "https://www.ejemplo.com/proyecto1",
      githubUrl: "https://github.com/tuusuario/proyecto1"
    },
    {
      title: "Proyecto 2",
      description: "Descripción del Proyecto 2.",
      imageUrl: "/ruta/a/imagen2.jpg",
      demoUrl: "https://www.ejemplo.com/proyecto2",
      githubUrl: "https://github.com/tuusuario/proyecto2"
    }
    // Puedes agregar más objetos de proyecto según sea necesario
  ];

  return (
    <section id="portfolio" className="section">
      <div className="container">
        <h2 className="section-title">Portafolio de Proyectos</h2>
        <div className="projects">
          {/* Mapeo de proyectos para renderizar cada uno */}
          {projects.map((project, index) => (
            <div className="project" key={index}>
              <img src={project.imageUrl} alt={project.title} className="project-image" />
              <div className="project-info">
                <h3 className="project-title">{project.title}</h3>
                <p className="project-description">{project.description}</p>
                <div className="project-links">
                  <a href={project.demoUrl} className="project-link" target="_blank" rel="noopener noreferrer">Demo</a>
                  <a href={project.githubUrl} className="project-link" target="_blank" rel="noopener noreferrer">Código Fuente</a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Portfolio;
