import React from 'react';

const FeaturedProjects = () => {
  // Array de objetos que representa los proyectos destacados
  const featuredProjects = [
    {
      title: "Proyecto Destacado 1",
      description: "Descripción del Proyecto Destacado 1.",
      imageUrl: "/ruta/a/imagen1.jpg",
      projectUrl: "https://www.ejemplo.com/proyecto1"
    },
    {
      title: "Proyecto Destacado 2",
      description: "Descripción del Proyecto Destacado 2.",
      imageUrl: "/ruta/a/imagen2.jpg",
      projectUrl: "https://www.ejemplo.com/proyecto2"
    }
    // Puedes agregar más objetos de proyecto según sea necesario
  ];

  return (
    <section id="featured-projects" className="section">
      <div className="container">
        <h2 className="section-title">Proyectos Destacados</h2>
        <div className="featured-projects">
          {/* Mapeo de proyectos para renderizar cada uno */}
          {featuredProjects.map((project, index) => (
            <div className="project" key={index}>
              <img src={project.imageUrl} alt={project.title} className="project-image" />
              <div className="project-info">
                <h3 className="project-title">{project.title}</h3>
                <p className="project-description">{project.description}</p>
                <a href={project.projectUrl} className="btn-view-project" target="_blank" rel="noopener noreferrer">Ver Proyecto</a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default FeaturedProjects;
